 <script language="javascript"> 
   function selectExp(seq){
     
 }
 
 
 
 function CheckOder(){
	 
	if($("input[name='rd'][type='radio'][checked=true]").length ==0){
		alert('please chose shipping method ');
		return false;
	}
	
	if($("input[name='payment'][type='radio'][checked=true]").length ==0){
		alert('please chose payment method ');
		return false;
	}
	
	return true;
 }
 
 
  function CheckOder(){
	 
	if($("input[name='rd'][type='radio'][checked=true]").length ==0){
		alert('请选择邮寄方式 ');
		return false;
	}
	
	if($("input[name='payment'][type='radio'][checked=true]").length ==0){
		alert('请选支付方式 ');
		return false;
	}
	
	return true;
 }
  
 function SubmitOrder()
 {
   if(!CheckOder())
	{
		return;
	}
	var param = {};
	param.ProductTMoney =  document.getElementById("price_color").innerHTML ;
	param.MailMoney="0" ;
	param.PaymentId =  $("input[name='payment'][type='radio'][checked=true]")[0].value;
    param.Shipment =  $("input[name='rd'][type='radio'][checked=true]")[0].value;
	param.Consigneename=$('#txtConsigneename').val() ;
	param.Receive_email=$('#txtReceive_email').val() ;
	param.Phone=$('#txtPhone').val() ;
	param.Receive_tel=$('#txtReceive_tel').val() ;
	param.address1=$('#txtaddress1').val() ;
	param.address2=$('#txtaddress2').val() ;
	param.Zip=$('#txtZip').val() ;
 
	$.getJSON("/User/AjaxHandler.aspx?MethodName=SubmitOrder&jsoncallback=?",param,function(json) {							 
		if(json.Success)
		{			
			window.location.href='/User/payment/PalyRouter.aspx?MyPlaymentId=' + json.MypaymentId + '&OrderId=' + json.OrderSeqno;
		}
		else
		{
			alert(json.Reaion);
			return false;
		}		
	});
 }
 
 
 
 </script>
 
 
