<script  type="text/javascript">
      $(document).ready(function() {          
		  $('#ajaxmsg').html('');	
		  $("#txtUserNo").blur( checkEmail );	  
		  $('#subsign').click( LoginSubmit );			 
      }); 
	  	  
	  function checkEmail()
	  { 
		 if($('#txtUserNo').val()==''){
			
		 }
	  }
	  
	  function LoginSubmit(){
		
		  if ($('#txtUserNo').val() == '')
		  {
			  alert('请输入邮件.');
			  $('#txtUserNo').focus();
			  return ;
		  }
		  $.getJSON("/User/AjaxHandler.aspx?MethodName=Login&jsoncallback=?",			  
			{
			userNo:encodeURIComponent($('#txtUserNo').val()),
			pwd:encodeURIComponent($('#passwordsign').val()) },
			function(json) {
				 
				if(json.Login){					
					if( $('#txtReturnUrl')!=null && $('#txtReturnUrl').val()!=''){
						 
						window.location.href = $('#txtReturnUrl').val();
					}else{		
						
						window.location.href = '/User/Adminframe.aspx'
					}
					$('#ajaxmsg').html('');
				}
				else{
					$('#ajaxmsg').html('<font color=red>对不起，你的邮件或者密码不正确！</font>');
					$('#ajaxmsg').show();
				}
			});
		}
  
  </script>
 <script  type="text/javascript">
      $(document).ready(function() {	  
		  $('#subCreate').click(SubmitRegedit);
		  $("#txtEmail").blur( function(){ 		  
			  return ChueckInputUserEmail();	  
		  } );	  
		  $("#txtPassword").blur( function(){ return CheckInputPwd(); } );	  
		  $("#txtPasswordConfirm").blur( function(){ return CheckInputPwd(); } );	  
		  
		  
      }); 
	  
	  function SubmitRegedit(){
			ChueckInputUserEmail();
			CheckInputPwd();
			
			if ($('#txtEmail_Val').is(":visible")){
				$('#txtEmail').focus();		
				return false;
			}
			if ($('#txtPassword_Val').is(":visible")){		   
				$('#txtPassword').focus();		
				return false;
			}
			if ($('#txtPasswordConfirm_Val').is(":visible")){		   
				$('#txtPasswordConfirm').focus();							
				return false;
			} 
			$.getJSON("/User/AjaxHandler.aspx?MethodName=RegeditUser&jsoncallback=?",			  
			{
				UserID:encodeURIComponent($('#txtEmail').val()),
				Pwd:encodeURIComponent($('#txtPassword').val()),				
				HomeTel:encodeURIComponent($('#txtHomeTel').val()),
				OfficeTel:encodeURIComponent($('#txtOfficeTel').val()),
				Moblie:encodeURIComponent($('#txtMoblie').val())
			},
			function(json) {
				if(json.MSG)
				{
					window.location.href = '/user/Adminframe.aspx'
				}
			});
		}
	  function ChueckInputUserEmail(){
		   if ($('#txtEmail').val()=='' || !isEmail($('#txtEmail').val(),'')){
				$('#txtEmail_Val').show();
		   }		   
		   if ($('#txtEmail').val()!='' && isEmail($('#txtEmail').val(),'')){						 
				$.getJSON("/User/AjaxHandler.aspx?MethodName=RegeditUser_DualisticJudge&jsoncallback=?",			  
				{UserID:encodeURIComponent($('#txtEmail').val())},
				function(json) {
					if (json.Exists){
						$('#txtEmail_Val').html($('#txtEmail').val()+' is already in use');
						$('#txtEmail_Val').show();
					}
					else
					{
						$('#txtEmail_Val').hide();
					}
				});
			}
			else
			{
				$('#txtEmail_Val').show();
			}
			
	  }
	  
	  function CheckInputPwd()
	  {	   
		   if ($('#txtPassword').val()==''){
				$('#txtPassword_Val').show();
		   }else if ($('#txtPassword').val()!=''){
				$('#txtPassword_Val').hide();
		   }	   
		   if ($('#txtPasswordConfirm').val()=='' || $('#txtPasswordConfirm').val() != $('#txtPassword').val()){
				$('#txtPasswordConfirm_Val').show();				
		   }else{
				 
				$('#txtPasswordConfirm_Val').hide();
		   }
		   return true;
		   
	  }
  
  </script>
