    <script type="text/javascript" language="javascript">

function SumitComment() {
	var content = $('#txtComments').val();
	var pguid = '|GUID|';
	if ($('#txtComments').val() == '') {
		alert("请填写评论内容！");
		return false;
	}
   
	$.post("/Wapper/ajaxHandler.aspx?MethodName=SubmitComment&randID=" + escape(new Date()),
		   {pguid: pguid, content: content, score1:5, score2:5, score3:5, score4:5, creator:"匿名"},
		  function(json) {	       
			  callbackSubmitComment(json);
	  });			
}

function callbackSubmitComment(res) {
	alert("已经提交，待管理员审核后，生效！");
	location.href = location.href;
}          
                                            
$(document).ready(function() {
$("a[rel=gal]").fancybox({
        'transitionIn': 'none',
        'transitionOut': 'none',
        'titlePosition': 'over',
        'titleFormat': function(title, currentArray, currentIndex, currentOpts) {
            return '<span id="fancybox-title-over">图像： ' + (currentIndex + 1) + ' / ' + currentArray.length + (title.length ? ' &nbsp; ' + title : '') + '</span>';
        }
    });    
    loadPriceforhotel();
});


function loadPriceforhotel() {

    _hid = '';   
    if (getUrlParam('tm1') == null) {
       if ($.cookie('tm1') != null){
          $('#tm1').val($.cookie('tm1'));
       }
       else {
          $('#tm1').val(new Date().Format("yyyy-MM-dd"));
       }       
    }
    else {
        $('#tm1').val(getUrlParam('tm1'));
    }
    if (getUrlParam('tm2') == null) {        
       if ($.cookie('tm2') != null){
          $('#tm2').val($.cookie('tm2'));
       }
       else {
          $('#tm2').val(new Date().Format("yyyy-MM-dd"));
       }       
    }
    else {
        $('#tm2').val(getUrlParam('tm2'));
    }


    _tm1 = $('#tm1').val(); //'2013-09-12';
    _tm2 = $('#tm2').val(); //'2013-09-13';

    $(".room_con[hid]").each(function() {
        if (_hid != '') {
            _hid += ',';
        }
        _hid += $(this).attr("hid");
    });
    loadPrice();
}
function changeTheOtherDate() {
    var url = document.URL;
  
    if (url.indexOf('?') != -1) {
        url = url.substring(0, url.indexOf('?'));
    }
    url += "?tm1=" + $('#tm1').val();
    url += "&tm2=" + $('#tm2').val();
    window.location = url;
}
        

Date.prototype.Format = function(fmt) { //author: meizz 
    var o = {
        "M+": this.getMonth() + 1, //月份 
        "d+": this.getDate(), //日 
        "h+": this.getHours(), //小时 
        "m+": this.getMinutes(), //分 
        "s+": this.getSeconds(), //秒 
        "q+": Math.floor((this.getMonth() + 3) / 3), //季度 
        "S": this.getMilliseconds() //毫秒 
    };
    if (/(y+)/.test(fmt)) fmt = fmt.replace(RegExp.$1, (this.getFullYear() + "").substr(4 - RegExp.$1.length));
    for (var k in o)
        if (new RegExp("(" + k + ")").test(fmt)) fmt = fmt.replace(RegExp.$1, (RegExp.$1.length == 1) ? (o[k]) : (("00" + o[k]).substr(("" + o[k]).length)));
    return fmt;
}

function getUrlParam(name) {
      var reg = new RegExp("(^|&)" + name + "=([^&]*)(&|$)");
      var r = window.location.search.substr(1).match(reg);
      if (r != null) return unescape(r[2]); return null;
}
